import React from 'react';
import { Card,Col,Row } from 'antd';
import  {useEffect} from "react";
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import  Actions from '../redux/actions';


function Homepage() {
  const photoData = useSelector((state) => state.post)
  const dispatch = useDispatch()


  // useEffect(() => {
  //  const getNews= async function(){
  //   try{
  //   const {data} = await axios.get("https://jsonplaceholder.typicode.com/photos");
  //   dispatch(Actions.homePageActions.setNews(data))
    
  // }catch(err){
  // }
  //  };
  //  getNews();
  // }, [dispatch])
  useEffect(() => {
    const data = axios.get("https://jsonplaceholder.typicode.com/photos").then((res)=> console.log(res)).catch((error) => console.log(error));
    if(data){
      dispatch(Actions.homePageActions.setNews(data))
    }
  }, [])
  
  return (

    <div className="site-card-wrapper">
      {photoData.length > 0 && photoData?.map((photo) => { 
    <Row gutter={16} key={photo.id}>
      <Col span={8}>
        <Card title={photo.title}   hoverable
        cover={<img alt="" src= {photo.thumbnailUrl} />}
        className="card">
        <p></p>
        </Card>
      </Col>
    </Row>
})}
  </div>
);
  
}
export default Homepage;
