import React from 'react';
import { Card,Col,Row } from 'antd';
import  {useEffect} from "react";
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import  Actions from '../redux/actions';


function Homepage() {
  const photoData = useSelector((state) => state.photo)
  const dispatch = useDispatch()


  useEffect(() => {
   const getNews= async function(){
    try{
    const {data} = await axios.get("https://jsonplaceholder.typicode.com/photos");
    dispatch(Actions.homePageActions.setNews(data))
    
  }catch(err){
  }
   };
   getNews();
  }, [dispatch])
  const p = photoData.filter((obj) => 15 > obj.id);
  return (

    <div className="site-card-wrapper">
      {p.length > 0 && Object.values(p).map((photo) => { 
    <Row gutter={16} key={photo.id}>
      <Col span={8}>
        {/* <Card title={photo.title}   hoverable
        cover={<img alt="" src= {photo.thumbnailUrl} />}
        className="card">
        <p></p>
        </Card> */}
        {photo.title}
      </Col>
    </Row>
})}
  </div>
);
  
}
export default Homepage;
