import React from 'react';
import { Card,Col,Row, Meta } from 'antd';
import  {useEffect} from "react";
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import  Actions from '../redux/actions';


function Homepage() {
  const photoData = useSelector((state) => state.photo)
  const dispatch = useDispatch()


  useEffect(() => {
   const getNews= async function(){
    try{
    const {data} = await axios.get("https://jsonplaceholder.typicode.com/photos");
    dispatch(Actions.homePageActions.setNews(data))
    
  }catch(err){
  }
   };
   getNews();
  }, [dispatch])
  const p = photoData.filter((obj) => 15 > obj.id);
  return (

    <div className="site-card-wrapper">
      {p.length > 0 && p.map((photo) => { 
    <Row gutter={16} key={photo.id}>
      <Col span={8}>
      <Card
    hoverable
    style={{
      width: 240,
    }}
    cover={<img alt="example" src={photo.thumbnailUrl} />}
  >
    <Meta title={photo.title} />
  </Card>
      </Col>
    </Row>
})}
  </div>
);
  
}
export default Homepage;
